"use client";

import { UserX } from "lucide-react";

export default function DeactivateModal({ open, onClose, onConfirm }: {
  open: boolean;
  onClose: () => void;
  onConfirm: () => void;
}) {
  if (!open) return null;

  return (
    <div className="fixed inset-0 bg-black/40 backdrop-blur-[2px] flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-2xl p-8 w-full max-w-md text-center shadow-xl">
        <div className="w-16 h-16 mx-auto rounded-full flex items-center justify-center mb-4">
          <TemporaryUserIcon size={36} color="#FF804C" />
        </div>
        <h3 className="text-xl font-semibold text-[#1A1A1A] mb-2">
          Deactivate Account?
        </h3>
        <p className="text-sm text-gray-500 leading-relaxed mb-8">
          Are you sure you want to deactivate your account? <br />
          You can reactivate it anytime by logging back in.
        </p>
        <div className="flex items-center gap-4">
          <button
            onClick={onClose}
            className="
              flex-1 py-3 
              rounded-lg border border-gray-300 
              text-sm font-medium 
              text-gray-700 
              hover:bg-gray-100 transition
            "
          >
            Cancel
          </button>

          {/* Deactivate Button */}
          <button
            onClick={onConfirm}
            className="
              flex-1 py-3 
              rounded-lg 
              text-sm font-medium 
              text-white 
              bg-[#F57C41] hover:bg-[#E96E33] transition
              flex items-center justify-center gap-2
            "
          >
            <TemporaryUserIcon size={17} color="white" />
            Deactivate
          </button>
        </div>

      </div>
    </div>
  );
}


